const express = require('expres');
const { Pool } = required('pg');
const app = express();

//Torna a pasta public e seu conteúdo ponto de acesso ao sistema
app.use(express.static('public'));

//Adiciona suporte para JSON no body das requisições HTTP
app.use(express.json());

const conexao = new Pool({
    host: "localhost",
    user: "postgres",
    password: "123456",
    database: "node",
    port: 5432
})

app.get("/", (req, res) => {
    res.send("Olá, mundo!");
});

applisten(3000, () => {
    console.log("Servidor rodando na porta 3000");
});